export default function NewsDetailPage() {
  const params = useParams()
  const [article, setArticle] = useState(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(false)

  useEffect(() => {
    if (!params.id) return

    const numericId = parseInt(params.id)
    if (isNaN(numericId)) {
      setError(true)
      setLoading(false)
      return
    }

    const foundArticle = newsItems.find(item => item.id === numericId)

    if (foundArticle) {
      setArticle(foundArticle)
    } else {
      setError(true)
    }

    setLoading(false)
  }, [params.id])

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-900 flex items-center justify-center text-white">
        加载中...
      </div>
    )
  }

  if (error || !article) {
    return (
      <div className="min-h-screen bg-gray-900 flex flex-col items-center justify-center text-white p-4">
        <h1 className="text-2xl font-bold mb-4">新闻不存在</h1>
        <p className="text-gray-400 mb-6">您访问的新闻不存在或已被删除</p>
        <Link href="/news" passHref>
          <Button className="bg-purple-600 hover:bg-purple-700 px-6 py-2">
            返回新闻列表
          </Button>
        </Link>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gray-900 text-white py-16">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="mb-8">
          <Link href="/news" passHref>
            <Button variant="outline" className="flex items-center gap-2 text-purple-300 border-purple-500/50 hover:bg-purple-500/10">
              <ArrowLeft size={16} />
              返回新闻列表
            </Button>
          </Link>
        </div>

        <div className="mb-6">
          <Badge className="bg-purple-500/20 text-purple-300 border-purple-500/30 mb-4">{article.category}</Badge>
          <h1 className="text-3xl md:text-4xl font-bold mb-4 text-white">{article.title}</h1>
          <div className="flex items-center gap-4 text-gray-400 text-sm mb-6">
            <span>{article.date}</span>
            <span>{article.readTime}</span>
          </div>

          <div className="mb-8">
            <img
              src={article.image || "/placeholder.svg"}
              alt={article.title}
              className="w-full h-64 md:h-96 object-cover rounded-lg"
            />
          </div>

          <div className="prose prose-lg prose-invert max-w-none">
            {/* 检查文章内容是否包含HTML标签，如果包含则使用dangerouslySetInnerHTML渲染 */}
            {article.content.includes('<h3>') || article.content.includes('<p>') ? (
              <div dangerouslySetInnerHTML={{ __html: article.content }} />
            ) : (
              article.content.split('\n\n').map((paragraph, index) => (
                <p key={index} className="mb-4 text-gray-300 leading-relaxed">
                  {paragraph}
                </p>
              ))
            )}
          </div>

          <div className="mt-8 pt-6 border-t border-gray-800">
            <div className="flex flex-wrap gap-2 mb-6">
              {article.tags.map((tag, tagIndex) => (
                <Badge key={tagIndex} variant="outline" className="border-gray-700 text-gray-400">
                  {tag}
                </Badge>
              ))}
            </div>

            <Link href="/news" passHref>
              <Button className="bg-gradient-to-r from-purple-600 to-blue-600 hover:from-purple-700 hover:to-blue-700 px-6 py-2">
                查看更多新闻
              </Button>
            </Link>
          </div>
        </div>
      </div>
    </div>
  )
}